mapper.select:
SELECT * from holiday


        AND (
        LOWER(name) LIKE CONCAT('%', LOWER(#{name}), '%')
        )


        AND (
        date = #{date}
        )



    OFFSET (#{page} - 1) * #{limit} FETCH NEXT #{limit} ROWS ONLY

mapper.update:
update holiday set
          name=#{name},
          note=#{note},
          date=#{date}
where code = #{code}
