mapper.select:
SELECT users.*, role.name as role, status.name as status, gender.name as gender, department.name as department, job_title.name as jobtitle, unit.name as unit, position.name as position FROM users LEFT JOIN role ON users.role_id = role.id LEFT JOIN status ON users.status_id = status.id LEFT JOIN gender ON users.gender_id = gender.id LEFT JOIN department ON users.department_code = department.code LEFT JOIN job_title ON users.job_title_code = job_title.code LEFT JOIN unit ON users.unit_code = unit.code LEFT JOIN position ON users.position_code = position.code order by updated_time desc
AND ( LOWER(users.fullname) LIKE CONCAT('%', LOWER(#{keyword}), '%') OR users.email LIKE CONCAT('%', #{keyword}, '%') OR users.employee_code LIKE CONCAT('%', #{keyword}, '%') )
AND users.department_code = #{departmentCode}
AND users.gender_id = #{genderId}
AND users.job_title_code = #{jobTitleCode}
AND users.status_id = #{statusId}
OFFSET (#{page} - 1) * #{limit} FETCH NEXT #{limit} ROWS ONLY
